var DragDrop = function (area, onDrop, options) {
	this.area = area;

	if (onDrop) {
		this.setOnDrop(onDrop);
	}

	if (options.onDragEnter) {
		this.setOnDragEnter(options.onDragEnter);
	}
	if (options.onDragOver) {
		this.setOnDragOver(options.onDragOver);
	}
	if (options.onDragLeave) {
		this.setOnDragLeave(options.onDragLeave);
	}

	return this;
}

DragDrop.prototype.setOnDragEnter = function (listener) {
	if (typeof listener !== 'function') {
		return false;
	}

	this.onDragEnter = listener;
	this.area.addEventListener('dragenter', this.onDragEnter.bind(this), false);
}
DragDrop.prototype.setOnDragOver = function (listener) {
	if (typeof listener !== 'function') {
		return false;
	}
	
	this.onDragOver = listener;
	this.area.addEventListener('dragover', this.onDragOver.bind(this), false);
}
DragDrop.prototype.setOnDragLeave = function (listener) {
	if (typeof listener !== 'function') {
		return false;
	}
	
	this.onDragLeave = listener;
	this.area.addEventListener('dragleave', this.onDragLeave.bind(this), false);
}
DragDrop.prototype.setOnDrop = function (listener) {
	if (typeof listener !== 'function') {
		return false;
	}
	
	this.onDrop = listener;
	this.area.addEventListener('drop', this.onDrop.bind(this), false);
}

document.addEventListener('DOMContentLoaded', function () {
	Array.prototype.forEach.call(document.querySelectorAll('[data-drag-drop-area]'), function (area) {
		new DragDrop(area, function (e) {
			e.preventDefault();
			console.log('drop', arguments);

			var formData = new FormData(this.area.form);
			var files = e.dataTransfer.files;
			console.dir(formData);
			console.dir(files);
			return false;
		}, {
			onDragEnter : function (e) {
				this.area.classList.add('hover');
				console.log('dragEnter', arguments);
			},
			onDragOver : function (e) {
				console.log('dragOver', arguments);
			},
			onDragLeave : function (e) {
				this.area.classList.remove('hover');
				console.log('dragLeave', arguments);
			}
		});
	});
}, false);
var EmailCompleter = function (input, trigger_char, domains) {
	this.input = input;
	this.trigger_char = trigger_char || this.defaults.trigger_char;
	this.domains = domains || this.defaults.domains;

	this.datalist = document.createElement('datalist');

	if (!input.id) {
		input.id = 'input-' + Date.now();
	}

	this.datalist.id = input.id + '-emailcompleter-datalist';
	this.input.setAttribute('list', this.datalist.id);
	this.input.nextSibling ? this.input.parentNode.insertBefore(this.datalist, this.input) : this.input.parentNode.appendChild(this.datalist);

	this.updateDatalist();

	this.input.addEventListener('input', this.updateDatalist.bind(this), false);

	return this;
}

EmailCompleter.prototype.defaults = {
	trigger_char: "@",
	domains: ["gmail.com", "orange.fr", "hotmail.fr", "free.fr", "yahoo.fr", "laposte.net", "voila.fr", "me.com", "hotmail.com", "icloud.com", "yahoo.com", "facebook.com"]
}

EmailCompleter.prototype.updateDatalist = function () {
	var address_parts = this.input.value.split(this.trigger_char);

	while (this.datalist.firstChild) {
		this.datalist.removeChild(this.datalist.firstChild);
	}

	if (address_parts.length < 2) {
		return;
	}

	this.domains.forEach((function (domain) {
		if (address_parts[1] && !domain.toLowerCase().substring(0, address_parts[1].length) != address_parts[1].toLowerCase()) {
			return;
		}

		var option = document.createElement('option');
		option.value = address_parts[0] + this.trigger_char + domain;
		this.datalist.appendChild(option);
	}).bind(this));
}


document.addEventListener('DOMContentLoaded', function () {
	Array.prototype.forEach.call(document.querySelectorAll('input[data-emailcompleter]'), function (input) {
		new EmailCompleter(input);
	});
}, false);
var MySexyBook = {
	currentScroll: 0,
	scrollTimeout: null,
	scrollCheckInterval: 1000/30,

	initialize: function () {
		document.documentElement.classList.add('js');

		this.currentScroll = 0;

		this.checkScroll();
	},
	checkScroll: function () {
		var scroll = window.scrollY;

		if (scroll !== this.currentScroll) {
			var difference = scroll - this.currentScroll;
			this.currentScroll = scroll;
			this.onScroll(difference);
		}

		this.scrollTimeout = setTimeout(this.checkScroll.bind(this), this.scrollCheckInterval);
	},
	onScroll: function (difference) {
		if (this.currentScroll > 0) {
			document.documentElement.classList.add('scrolled');
		}
		else {
			document.documentElement.classList.remove('scrolled');
		}
	}
};

document.addEventListener('DOMContentLoaded', MySexyBook.initialize.bind(MySexyBook), false);

var album_elem = document.querySelector('#album-container');

if (album_elem) {
	var viewer = {
		album_elem: album_elem,
		initialize: function () {
			this.user = {
				_id: this.album_elem.dataset.userId
			};
			this.album = {
				_id: this.album_elem.dataset.albumId
			};

			this.album_info_elem = this.album_elem.querySelector('.album-info')
			
			var album_desc_elem = this.album_elem.querySelector('.album-description')
			if (album_desc_elem && album_desc_elem.innerHTML) {
				this.album.description = album_desc_elem.innerHTML;
			}

			this.form_container = this.album_elem.querySelector('#form-photo');

			if (this.form_container) {
				this.album_info_elem.appendChild(this.form_container);
			}

			this.photos_container = this.album_elem.querySelector('.album-photos');

			this.thumbs_container = document.createElement('ul');
			this.thumbs_container.classList.add('album-thumbs');

			this.photos_elem = this.photos_container.querySelectorAll('li[id^="photo-"]');
			this.photos = new Array(this.photos_elem.length);

			if (this.photos_container.nextSibling) {
				this.photos_container.parentNode.insertBefore(this.thumbs_container, this.photos_container.nextSibling);
			}
			else {
				this.photos_container.parentNode.appendChild(this.thumbs_container);
			}

			this.thumbs_sizes = this.thumbs_container.offsetHeight || (16 * 5.5);

			Array.prototype.forEach.call(this.photos_elem, function (li, index) {
				var photo = {
					_id: li.id.replace(/^photo-/, '')
				};

				var title_elem = li.querySelector('.photo-caption')
				if (title_elem && title_elem.innerHTML) {
					photo.title = title_elem.innerHTML;
				}

				if (!li.dataset || !li.dataset.photoSrc) {
					console.error('Nom photo introuvable', li);
					return;
				}
				photo.src = li.dataset.photoSrc;

				if (index === 0) {
					if (!window.location.hash || !document.querySelector(window.location.hash) || !document.querySelector(window.location.hash).dataset || !document.querySelector(window.location.hash).dataset.photoSrc) {
						window.location.hash = '#' + li.id;
					}
				}
				else {
					// li.classList.add('hidden');
				}

				var thumb_elem = document.createElement('li');
				thumb_elem.dataset.photoId = photo._id;

				var thumb_link = document.createElement('a');
				thumb_link.href = '#' + li.id;

				var thumb_img = document.createElement('img');
				thumb_img.src = '/photo/' + this.user._id + '/' + this.album._id + '/' + this.thumbs_sizes + 'x' + this.thumbs_sizes + 'x1/' + photo.src;
				thumb_img.alt = photo.title || '';

				thumb_link.appendChild(thumb_img);
				thumb_elem.appendChild(thumb_link);
				this.thumbs_container.appendChild(thumb_elem);

				var figure = li.querySelector('.photo')
				if (figure) {
					figure.removeAttribute('tabindex');
				}

				this.photos.push(photo);
			}, this);

			this.photos_container.parentNode.insertBefore(this.photos_container, this.photos_container.parentNode.firstChild);

			setTimeout((function () {
				var photo_width = this.photos_container.clientHeight;
				var photo_height = this.photos_container.clientHeight;
				Array.prototype.forEach.call(this.photos_elem, function (li, index) {
					var photo_big = li.querySelector('picture');

					var photo_width = li.firstElementChild.clientWidth;
					var photo_height = li.firstElementChild.clientHeight;
				// console.dir(photo_width);
				// console.dir(photo_height);

					// console.dir(photo_big);

					if (photo_big) {
						photo_big.style.maxWidth = photo_width + 'px';
						photo_big.style.maxHeight = photo_height + 'px';
					}

					Array.prototype.forEach.call(photo_big.children, function (src) {
						// console.dir(src);
						if (src.style) {
							src.style.maxWidth = photo_width + 'px';
							src.style.maxHeight = photo_height + 'px';
						}
					})
				}, this);
			}).bind(this), 0);
		}
	};

	// viewer.initialize();
}
String.prototype.toSlug = function () {
	var value = "" + this; // On évite d'écraser la chaîne d'origine en créant une copie de travail

	// `re` correspond à la regex de recherche, `ch` étant le caractère correspondant (celui à utiliser en remplacement)
	var accents = [
		{ re: /[\xC0-\xC6]/g, ch: 'A' },
		{ re: /[\xE0-\xE6]/g, ch: 'a' },
		{ re: /[\xC8-\xCB]/g, ch: 'E' },
		{ re: /[\xE8-\xEB]/g, ch: 'e' },
		{ re: /[\xCC-\xCF]/g, ch: 'I' },
		{ re: /[\xEC-\xEF]/g, ch: 'i' },
		{ re: /[\xD2-\xD6]/g, ch: 'O' },
		{ re: /[\xF2-\xF6]/g, ch: 'o' },
		{ re: /[\xD9-\xDC]/g, ch: 'U' },
		{ re: /[\xF9-\xFC]/g, ch: 'u' },
		{ re: /[\xC7-\xE7]/g, ch: 'c' },
		{ re: /[\xD1]/g, ch: 'N' },
		{ re: /[\xF1]/g, ch: 'n' }
	];

	// On convertit les caractères accentués en leurs équivalent alphanumériques (via le tableau défini juste au-dessus)
	for (var i=0, nb=accents.length; i<nb; i++) {
		value = value.replace(accents[i].re, accents[i].ch);
	}

	// On passe en minuscules, on remplace les espaces par des tirets, on enlève les caractères non-alphanumériques puis on enlève les tirets multiples
	return value.toLowerCase()
				.replace(/\s+/g, '-')
				.replace(/[^a-z0-9-]/g, '')
				.replace(/\-{2,}/g,'-');
};



Object.prototype.extend = function(obj) {
	for (var i in obj) {
		this[i] = obj[i];
	}
	return this;
};
